        with open(out) as f:
            lines = f.readlines()
            assert len(lines) == 5
            assert lines[0].strip().split("\t") == ["contig","domains","sequence","length","topology"]
            for l_num in range(1,len(lines)):
                l = lines[l_num]
                l = l.strip().split("\t")
                assert f"pDONR201_{l_num}" == l[0]
